mapper.select:
SELECT P.emp_id, P.name, P.grade_name, P.branch_name, P.dept_name, P.mail FROM ( SELECT ROW_NUMBER() OVER(ORDER BY G.grade_no ASC) AS RNO, E.emp_id, E.name, G.grade_name, B.branch_name, D.dept_name, E.mail FROM tbl_employee E JOIN tbl_grade G ON E.fk_grade_no = G.grade_no JOIN tbl_branch B ON E.fk_branch_no = B.branch_no JOIN tbl_department D ON E.fk_dept_id = D.dept_id WHERE status != 3
AND lower(dept_name) LIKE '%'||lower(#{searchWord})||'%'
AND lower(grade_name) LIKE '%'||lower(#{searchWord})||'%'
AND lower(branch_name) LIKE '%'||lower(#{searchWord})||'%'
AND lower(name) LIKE '%'||lower(#{searchWord})||'%'
) P WHERE P.RNO BETWEEN #{startRno} AND #{endRno}

SELECT count(*)
FROM tbl_employee
WHERE mail = #{mail}

SELECT manager_id
FROM tbl_department
WHERE dept_id = #{dept_id}

mapper.insert:
INSERT INTO tbl_employee ( emp_id, fk_dept_id, name, password, tel, personal_mail, hire_date, mail, postcode, address, detailaddress, extraaddress, gender, birthday, fk_grade_no, status, pwdchangedate, pwdchangestatus, leave_count, profile_img, fk_branch_no, leader_id ) VALUES (TO_NUMBER(EXTRACT(YEAR FROM SYSDATE) || LPAD(emp_seq.NEXTVAL, 3, '0')), #{dept_id}, #{name}, '03ac674216f3e15c761ee1a5e255f067953623c8b388b4459e13f978d7c846f4', #{tel}, #{personal_mail}, default, #{mail}, #{postcode}, #{address}, #{detailaddress}, #{extraaddress}, #{gender}, #{birthday}, #{grade_no}, default, null, default, default, #{profile_img }, #{branch_no},
default
#{leader_id}
)

mapper.update:
UPDATE tbl_employee 
SET name = #{name}, birthday = #{birthday}, gender = #{gender}, personal_mail = #{personal_mail},
		   mail = #{mail}, tel = #{tel}, postcode = #{postcode}, detailaddress = #{detailaddress},
		   extraaddress = #{extraaddress}, fk_branch_no = #{branch_no}, fk_dept_id = #{dept_id},
		   fk_grade_no = #{grade_no}, status = #{empstatus}, profile_img = #{profile_img}, leader_id = #{leader_id}
WHERE emp_id = #{emp_id}
